mapper.select:
SELECT i.*,u.id u_id,u.member,u.portyaitl,u.securityMoney,u.userName, t.id t_id,t.typeName,st.id st_id,st.costName,r.id r_id,r.roleName, a.id a_id,p.id p_id,p.prow,c.id c_id,c.city,co.id co_id,co.country FROM shoot_info i INNER JOIN shoot_user u ON i.userId=u.id INNER JOIN shoot_type t ON i.typeId=t.id INNER JOIN shoot_cost st ON i.costId=st.id INNER JOIN shoot_user_role ur ON ur.userId=u.id INNER JOIN shoot_role r ON r.id=ur.roleId INNER JOIN shoot_address a ON a.id=i.shootAddressId INNER JOIN shoot_prow p ON a.prow_id=p.id INNER JOIN shoot_city c ON a.city_id=c.id INNER JOIN shoot_country co ON a.country_id=co.id
i.stateId=1
and c.city=#{city}
and st.id=#{costId}
and r.id=#{roleId}
and u.sex=#{sex}
order by i.creationDate desc
