mapper.select:
SELECT le.id le_id,le.creationDate,le.content,le.infoId, u.id u_id,u.portyaitl,u.userName, r.id r_id,r.roleName,p.id p_id,p.prow, c.id c_id,c.city,co.id co_id,co.country, i.id i_id,i.content i_content FROM shoot_letter le INNER JOIN shoot_user u ON u.id=le.sendUserId INNER JOIN shoot_user_role ur ON ur.userId=u.id INNER JOIN shoot_role r ON r.id=ur.roleId INNER JOIN shoot_address a ON a.id=u.shootAddressId INNER JOIN shoot_prow p ON p.id=a.prow_id INNER JOIN shoot_city c ON c.id=a.city_id INNER JOIN shoot_country co ON co.id=a.country_id inner join shoot_info i on i.id=le.infoId inner join shoot_state st on i.stateId=st.id WHERE le.putUserId=#{putUserId} and i.stateId=2
and le.infoId=#{infoId}
group by u.id order by le.creationDate desc

SELECT le.id le_id,le.creationDate,le.content,le.infoId, u.id u_id,u.portyaitl,u.userName, r.id r_id,r.roleName,p.id p_id,p.prow, c.id c_id,c.city,co.id co_id,co.country, i.id i_id,i.content i_content FROM shoot_letter le INNER JOIN shoot_user u ON u.id=le.sendUserId INNER JOIN shoot_user_role ur ON ur.userId=u.id INNER JOIN shoot_role r ON r.id=ur.roleId INNER JOIN shoot_address a ON a.id=u.shootAddressId INNER JOIN shoot_prow p ON p.id=a.prow_id INNER JOIN shoot_city c ON c.id=a.city_id INNER JOIN shoot_country co ON co.id=a.country_id inner join shoot_info i on i.id=le.infoId inner join shoot_cost st on st.id=i.costId inner join shoot_state ta on ta.id=i.stateId WHERE le.putUserId=#{putUserId}
and st.costName=#{costName}
and i.stateId=2 GROUP BY u.id ORDER BY le.creationDate ASC
