mapper.select:
SELECT u.*,r.id r_id,r.roleName,pe.id pe_id,pe.permissionName FROM shoot_user u INNER JOIN shoot_user_role ur ON u.id=ur.userId INNER JOIN shoot_role r ON ur.roleId=r.id INNER JOIN shoot_role_permission rp ON r.id=rp.roleId INNER JOIN shoot_permission pe ON rp.PermissionId=pe.id where 1=1
and ur.userid=#{id}
and u.id!=1
